import React, {Component} from 'react';
import './counter.css'

class Counter extends Component {

    //State
    state = {
        value: this.props.value,
        tags: ['tag1','tag2','tag3']
    }

    // Prop methods
    onDelete;

    constructor(props) {
        super(props);
        this.handleIncrement = this.handleIncrement.bind(this);
    }

    //HTML
    render() {
        return (
            <div className="flex-column align">
                <span className={this.getBadgeClasses()}>{this.formatCount()}</span>
                <button className="btn btn-secondary btn-sm m-2" onClick={ () => this.handleIncrement()}>Increment</button>
                <button className="btn btn-danger btn-sm m-2" onClick={this.props.onDelete}>Delete</button>
            </div>
        );
    }

    //Methods
    formatCount() {
        const { value } = this.state;
        return value === 0 ? "Zero" : value;
    };
    getBadgeClasses() {
        let classes = "warningButtonText badge m-2 badge-";
        classes += this.state.value === 0 ? "warning" : "primary"
        return classes;
    };
    handleIncrement = () => {
        this.setState({ value: this.state.value + 1})
    };
    // runs when the page is updates
    componentDidUpdate(prevProps, prevState, snapshot) {
        console.log(this.state.value)
    };
}

Counter.propTypes = {};
export default Counter;
